definition.script:
#!/usr/bin/env groovy
// ******************************* stage 'Install Rally' // *******************************
echo 'Provisioning an onMetal server and preparing it to run OSA' node('josh-aio-ansible-temp') { sh """ ssh -o StrictHostKeyChecking=no root@${localhost} ''' touch /root/ cd git clone https://github.com/openstack/rally.git ./install-rally.sh ''' """ }
// ******************************* stage 'Run Prime Jobs' // ******************************* echo 'Provisioning an onMetal server and preparing it to run OSA' node('josh-aio-ansible-temp') { sh """ ssh -o StrictHostKeyChecking=no root@${localhost} ''' touch /root/ cd source openrc rally deployment create —fromenv —name=existing git clone https://github.com/jwatford/rally-scenarios.git cd rally_scenarios/ rally task start osic-keystone-prime-scenario.json --task-args-file args.yaml rally task start osic-nova-1-server-scenario.json --task-args-file args.yaml ''' """ }
// ******************************* stage 'Run Benchmarks' // ******************************* echo 'Provisioning an onMetal server and preparing it to run OSA' node('josh-aio-ansible-temp') { sh """ ssh -o StrictHostKeyChecking=no root@${localhost} ''' touch /root/ cd source openrc rally deployment use --deployment existing rally task start benchmark.json --task-args-file args.yaml rally task report 2a08d1fb-1964-44e4-a30d-3b370e020678 --junit --out results.xml rally task report 2a08d1fb-1964-44e4-a30d-3b370e020678 --html-static --out results.html rally task results 2a08d1fb-1964-44e4-a30d-3b370e020678 > results.json ''' """ }
