definition.script:
#!/usr/bin/env groovy

// *******************************
stage 'Install Rally'
// *******************************

echo 'Provisioning an onMetal server and preparing it to run OSA'
node('josh-aio-ansible-temp') {
    sh """
      ssh -o StrictHostKeyChecking=no root@localhost '''
        touch /root/
        cd
        git clone https://github.com/openstack/rally.git
        cd rally
        ./install-rally.sh
      '''
    """
}
